sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        return;
    }
    if (!newValue=="") {

        g_form.setSectionDisplay("device_details", true);
        g_form.setSectionDisplay("delivery_details", true);
    }
    var ag = new GlideAjax("fetchAdditionalDetails");
    ag.addParam("sysparm_name", 'additionalDetails');
    ag.addParam("sysparm_devname", g_form.getDisplayBox("u_reference_1").value);
    ag.getXMLAnswer(function(response) {
        var result = JSON.parse(response);
        var fields = ['u_string_5', 'u_string_7', 'u_price_8', 'u_glide_date_6'];
        fields.map((field, index) => {
            g_form.setValue(field, Object.values(result)[index]);
        })
    })
}
