sys_script_include.script:
var fetchAdditionalDetails = Class.create();
fetchAdditionalDetails.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
    additionalDetails: function() {
        var itemdetails = {};
        var devName = this.getParameter("sysparm_devname");
        var cus = new GlideRecord("x_1732636_device_r_ai_devices");
        cus.addQuery("name", devName);
        cus.query();
        if (cus.next()) {
            itemdetails.u_string_5 = cus.model_number.toString();
            itemdetails.u_string_7 = cus.short_description.toString();
            itemdetails.u_price_8 = cus.u_price_2.toString();
            itemdetails.u_glide_date_6 = cus.warranty_expiration.toString();
        }
        return JSON.stringify(itemdetails);
    },
    type: 'fetchAdditionalDetails'
});
